                .build())
            .scalingDownPolicies(ElastigroupScalingDownPolicyArgs.builder()
                .policyName("Default Scaling Down Policy")
                .metricName("DefaultQueuesDepth")
                .statistic("average")
                .unit("none")
                .adjustment(1)
                .namespace("custom")
                .threshold(10)
                .period(60)
                .evaluationPeriods(10)
                .cooldown(300)
                .build())
            .tags(            
                ElastigroupTagArgs.builder()
                    .key("Env")
