{{% examples %}}
## Example Usage
{{% example %}}

```typescript
import * as pulumi from "@pulumi/pulumi";
import * as spotinst from "@pulumi/spotinst";

// Create an Elastigroup
const default_elastigroup = new spotinst.aws.Elastigroup("default-elastigroup", {
    capacityUnit: "weight",
    cpuCredits: "unlimited",
    cpuOptions: {
        threadsPerCore: 1,
    },
    description: "created by Pulumi",
    desiredCapacity: 0,
    ebsOptimized: false,
    enableMonitoring: false,
    fallbackToOndemand: false,
    iamInstanceProfile: "iam-profile",
    imageId: "ami-a27d8fda",
    instanceTypesOndemand: "m3.2xlarge",
    instanceTypesPreferredSpots: ["m3.xlarge"],
    instanceTypesSpots: [
        "m3.xlarge",
        "m3.2xlarge",
    ],
    instanceTypesWeights: [
        {
            instanceType: "c3.large",
            weight: 10,
        },
        {
            instanceType: "c4.xlarge",
            weight: 16,
        },
    ],
    keyName: "my-key.ssh",
    maxSize: 0,
    metadataOptions: {
        httpPutResponseHopLimit: 10,
        httpTokens: "optional",
    },
    minSize: 0,
    minimumInstanceLifetime: 12,
    orientation: "balanced",
    placementTenancy: "default",
    product: "Linux/UNIX",
    region: "us-west-2",
    scalingDownPolicies: [{
        adjustment: "1",
        cooldown: 300,
        evaluationPeriods: 10,
        metricName: "DefaultQueuesDepth",
        namespace: "custom",
        period: 60,
        policyName: "Default Scaling Down Policy",
        statistic: "average",
        threshold: 10,
        unit: "none",
    }],
    scalingStrategies: [{
        terminateAtEndOfBillingHour: true,
        terminationPolicy: "default",
    }],
    scalingUpPolicies: [{
        adjustment: "1",
        cooldown: 300,
        evaluationPeriods: 5,
        metricName: "DefaultQueuesDepth",
        namespace: "custom",
        period: 60,
        policyName: "Default Scaling Up Policy",
        statistic: "average",
        threshold: 100,
        unit: "none",
    }],
    securityGroups: ["sg-123456"],
    subnetIds: [
        "sb-123456",
        "sb-456789",
    ],
    tags: [
        {
            key: "Env",
            value: "production",
        },
        {
            key: "Name",
            value: "default-production",
        },
        {
            key: "Project",
            value: "app_v2",
        },
    ],
    userData: "echo hello world",
    waitForCapacity: 5,
    waitForCapacityTimeout: 300,
}, { ignoreChanges: ["desiredCapacity"] });
